import React, { useState } from "react";
import {
    Drawer,
    IconButton,
    List,
    ListItem,
    ListItemText,
    // makeStyles
} from '@mui/material';
import { Link } from "react-router-dom";

// const useStyles = makeStyles(() => ({
//     link: {
//         textDecoration: "none",
//         color: "blue",
//         fontSize: "20px",
//     },
//     icon: {
//         color: "white"
//     }
// }));

function NavDrawer({ token }) {
    // const classes = useStyles();
    const [openDrawer, setOpenDrawer] = useState(false);
    return (
        <>
            <Drawer
                open={openDrawer}
                onClose={() => setOpenDrawer(false)}
            >
                <List>
                    <ListItem onClick={() => setOpenDrawer(false)}>
                        <ListItemText>
                            <Link to="/">Home</Link>
                        </ListItemText>
                    </ListItem>
                    <ListItem onClick={() => setOpenDrawer(false)}>
                        <ListItemText>
                            <Link to='/posts'>Posts</Link>
                        </ListItemText>
                    </ListItem>
                    {
                        token ? (
                            <>
                                <ListItem>
                                    <ListItemText>
                                        <Link to='/profile'>Profile</Link>
                                    </ListItemText>
                                </ListItem>
                                <ListItem>
                                    <ListItemText>
                                        <Link to='/' onClick={() => logout()}>Logout</Link>
                                    </ListItemText>
                                </ListItem>
                            </>
                        ) : (
                            <>
                            <ListItem>
                                <ListItemText>
                                <Link to='/register'>New Account</Link>
                                </ListItemText>
                            </ListItem>
                            <ListItem>
                                <ListItemText>
                                <Link to='/login'>Sign In</Link>
                                </ListItemText>
                            </ListItem>
                            </>
                        )
                    }
                </List>
            </Drawer>
            <IconButton onClick={() => setOpenDrawer(!openDrawer)}>
                <MenuIcon />
            </IconButton>
        </>
    );
}
export default NavDrawer;
