import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
/* we only need ReactDOM once. All new components will need to import React*/
import './style.css'
import { getPosts, getUserInfo } from './api/index.js'
import {
    Route,
    BrowserRouter,
    Routes,
    useNavigate
} from 'react-router-dom';
import {
    Navbar,
    Posts,
    Profile,
    Home,
    Register,
    Login,
    NewPost,
    SinglePostView,
} from './components';
import { CssBaseline } from '@mui/material';

const App = () => {
    const [posts, setPosts] = useState([]);
    const [token, setToken] = useState('');
    const [user, setUser] = useState({});

    const navigate = useNavigate();


    function logout() {
        window.localStorage.removeItem('token');
        setToken('');
        setUser({});
    }

    async function fetchPosts() {
        const results = await getPosts(token)
        setPosts(results.data.posts);
    }

    async function getMe() {
        const storedToken = window.localStorage.getItem('token');

        if (!token) {
            if (storedToken) {
                setToken(storedToken);
            }
            return;
        }

        const results = await getUserInfo(token)
        if (results.success) {
            setUser(results.data);
        } else {
            console.log(results.error.message);
        }
    }

    useEffect(() => {
        fetchPosts();
    }, [token])

    useEffect(() => {
        getMe();
    }, [token])

    return (
        <div>
            <Navbar logout={logout} token={token}/>
            <Routes>
                <Route path='/' element={<Home />} />
                <Route path='/posts' element={<Posts posts={posts} token={token} />} />
                <Route path='/posts/:id' element={<SinglePostView />} />
                <Route path='/profile' element={<Profile user={user}/>} />
                <Route exact path='/posts/new-post' element={<NewPost token={token}/>} />
                <Route
                    path='/register'
                    element={<Register
                        setToken={setToken}
                        token={token}
                        navigate={navigate}
                    />}
                />
                <Route
                    path='/login'
                    element={<Login
                        setToken={setToken}
                        navigate={navigate}
                    />}
                />
            </Routes>
        </div>
    )
}

const container = document.querySelector('#container');
const root = ReactDOM.createRoot(container);
root.render(
    <BrowserRouter>
        <CssBaseline>
            <App />
        </CssBaseline>
    </BrowserRouter>
)

