import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import {
  Paper,
  Button,
  Card,
  CardContent,
  CardActions,
  TextField,
} from '@mui/material';
import styles from '../style.css';


const Posts = ({ posts, token }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const postMatches = (post, string) => {
    const { title, description } = post;

    if ((title.toLowerCase().includes(string.toLowerCase())) || description.toLowerCase().includes(string.toLowerCase())) {
      return post;
    }
  }

  const filteredPosts = posts.filter(post => postMatches(post, searchTerm));
  const postsToDisplay = searchTerm.length ? filteredPosts : posts;

  const paperStyle = {
    padding: 20,
    width: 350,
    margin: '20px auto'
  }

  if (postsToDisplay.length) {
    return (
      <div id='postContainer'>
        <form className='searchBar' onSubmit={(event) => {
          event.preventDefault();
        }}>
          <TextField
            type='text'
            placeholder='Enter search here...'
            onChange={(event) => setSearchTerm(event.target.value)}>
          </TextField>
        </form>

        {
          token ? (
            <Link className='newPostButton' to='/posts/new-post'>
              <Button variant='contained'>Create New Post</Button>
            </Link>
          ) : (
            <p><Link to='/register'>Register</Link> or <Link to='/login'>login</Link> to create a post.</p>
          )
        }

        {
          postsToDisplay.map((post) => {
            const { description, location, price, title, willDeliver, _id, isAuthor } = post;
            return (
              <div className='posts'>
                <Paper elevation={10} style={paperStyle}>
                  <Card
                    className={styles.singlePost}
                    key={_id}>
                    <CardContent>
                      <h3 className='postTitle'>{title}</h3>
                      <p className='postInfo'>Description:</p>{description}
                      <p className='postInfo'>Price:</p>{price}
                      <p className='postInfo'>Location:</p>{location}
                      <p className='postInfo'>Delivery:</p>{willDeliver}
                    </CardContent>
                    <CardActions>
                      {
                        isAuthor ? (
                          <>
                            <Link to={`/posts/edit-post/${_id}`}><Button>Edit Post</Button></Link>
                            <Link to={`/posts/${_id}`}><Button>View Post</Button></Link>
                          </>
                        ) : (
                          <Link to={`/posts/${_id}`}><Button>View Post</Button></Link>
                        )
                      }
                    </CardActions>
                  </Card>
                </Paper>
              </div>
            )
          })
        }
      </div>
    )
  } else {
    return (
      <h1>Loading posts...</h1>
    )
  }

}

export default Posts;
