import React, { useState } from 'react';
import { useParams } from 'react-router-dom';


const EditPost = ({ token, posts }) => {
    const [newTitle, setNewTitle] = useState('');
    const [newDescription, setNewDescription] = useState('');
    const [newPrice, setNewPrice] = useState('');
    const [newLocation, setNewLocation] = useState('On Request');
    const [newWillDeliver, setNewWillDeliver] = useState(false);

    const { postID } = useParams();
    const [currentPost] = posts.filter(post => post._id === postID);
    const { title, description, location, price, willDeliver, _id, isAuthor } = currentPost;

  return (
    <form onSubmit={(event) => {
        event.preventDefault();
      }}>
        <input 
          type='text'
          placeholder='Title'
          onChange={(event) => setNewTitle(event.target.value)}
        />
        <input 
          type='text'
          placeholder='Description'
          onChange={(event) => setNewDescription(event.target.value)}
        />
        <input 
          type='text'
          placeholder='Price'
          onChange={(event) => setNewPrice(event.target.value)}
        />
        <input 
          type='text'
          placeholder='Location'
          onChange={(event) => setNewLocation(event.target.value)}
        />
        <label>Will Deliver?</label>
        <input 
          type='checkbox'
          placeholder='Yes'
          onChange={(event) => setWillDeliver(event.target.check)}
        />
        <button type='submit'>Update</button>
      </form>
  )
}

export default EditPost;
